<script>
let startTime, timeout
export default {
  name: `magic-icon-component`,
  props: [ 'show' ],
  data() {
    return {
      showMagician: false,
      showMagicianAlt: false,
    }
  },
  watch: {
    show() {
      if (this.show && !this.showMagician) {
        startTime = Date.now()
        this.startWaving()
      } else if (!this.show) {
        this.showMagician = false
        clearTimeout(timeout)
      }
    },
  },
  methods: {
    startWaving() {
      const totalTime = Date.now() - startTime
      const cycleTime = totalTime % 400
      if (cycleTime > 200) {
        this.showMagician = false
        this.showMagicianAlt = true
      } else {
        this.showMagician = true
        this.showMagicianAlt = false
      }
      if (totalTime < 3000) {
        timeout = setTimeout(this.startWaving.bind(this), 200)
      } else {
        this.showMagician = true
        this.showMagicianAlt = false
      }
    }
  }
}
</script>
